#include <iostream>
#include <queue>

using namespace std;

int n, t, r, c;
int memo[2][510][510];
int to[2][4] = { {-1, 1, 0, 0}, {0, 0, -1, 1} };
char map[510][510];

struct DDD {
	int row;
	int col;
	bool state;
	int value;
};

struct qcmp {
	bool operator()(DDD arg1, DDD arg2) {
		return arg1.value > arg2.value;
	}
};

priority_queue<DDD, vector<DDD>, qcmp> q;

void bfs()
{
	int i, k;
	DDD cur, next;

	memo[0][1][1] = 0;
	next.row = next.col = 1;
	next.state = false;
	next.value = 0;
	q.push(next);

	while (!q.empty()) {
		cur = q.top();
		q.pop();
		if (memo[cur.state][cur.row][cur.col] < cur.value) continue;
		// Super
		for (i = 0; i < 4; i++) {
			for (k = 1; k <= n; k++) {
				int to_row = cur.row + to[0][i] * k;
				int to_col = cur.col + to[1][i] * k;

				if (to_row > n || to_col > n || to_row < 1 || to_col < 1) break;
				if (map[to_row][to_col] != '#') continue;

				if (cur.state) {
					if (memo[1][to_row][to_col] > memo[1][cur.row][cur.col] + 1) {
						memo[1][to_row][to_col] = memo[1][cur.row][cur.col] + 1;
						next.row = to_row;
						next.col = to_col;
						next.state = true;
						next.value = memo[1][to_row][to_col];
						q.push(next);
					}
				}
				else {
					if (memo[1][to_row][to_col] > memo[0][cur.row][cur.col] + 1 + t) {
						memo[1][to_row][to_col] = memo[0][cur.row][cur.col] + 1 + t;
						next.row = to_row;
						next.col = to_col;
						next.state = true;
						next.value = memo[1][to_row][to_col];
						q.push(next);
					}
				}
				break;
			}
		}
		// Normal
		for (i = 0; i < 4; i++) {
			int to_row = cur.row + to[0][i];
			int to_col = cur.col + to[1][i];

			if (to_row > n || to_col > n || to_row < 1 || to_col < 1) continue;

			if (cur.state) {
				if (memo[0][to_row][to_col] > memo[1][cur.row][cur.col] + 1) {
					memo[0][to_row][to_col] = memo[1][cur.row][cur.col] + 1;
					next.row = to_row;
					next.col = to_col;
					next.state = false;
					next.value = memo[0][to_row][to_col];
					q.push(next);
				}
			}
			else {
				if (memo[0][to_row][to_col] > memo[0][cur.row][cur.col] + 1) {
					memo[0][to_row][to_col] = memo[0][cur.row][cur.col] + 1;
					next.row = to_row;
					next.col = to_col;
					next.state = false;
					next.value = memo[0][to_row][to_col];
					q.push(next);
				}
			}
		}
	}
}

int main()
{
	int i, j;

	cin >> n >> t >> r >> c;

	for (i = 1; i <= n; i++) {
		for (j = 1; j <= n; j++) {
			memo[0][i][j] = 1000000000;
			memo[1][i][j] = 1000000000;
		}
	}

	for (i = 1; i <= n; i++) {
		cin >> map[i] + 1;
	}

	bfs();

	if (memo[0][r][c] < memo[1][r][c]) cout << memo[0][r][c];
	else cout << memo[1][r][c];

	return 0;
}
